import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  background: #f9f9f9;

  .glider-prev,
  .glider-next {
    top: 75px;
  }

  .glider-prev {
    left: -30px;
  }

  .glider-next {
    right: -30px;
  }

  .glider {
    height: 500px;
  }
  .glider-contain {
    margin-bottom: 25px;
  }
  .glider-dots {
    margin-top: 15px;
  }
  .glider-track {
    height: 100%;
  }
  .glider-slide {
    display: flex;
    justify-content: center;
    align-items: center;
    border: 1px solid #f5f5f5;
  }

  .slide {
    background-color: palegreen;
    color: darkgreen;
    font-size: 3em;
    font-weight: bold;
  }
`;
