import styled from 'styled-components';

export const Container = styled.section`
  max-width: 1200px;
  margin: 0 auto;

  margin-top: 8rem;
`;

export const Title = styled.h2`
  width: 100%;
  color: #041e50;
  text-align: center;
  text-transform: uppercase;

  font-size: 2rem;
`;

export const Description = styled.p`
  line-height: 1rem;
  color: #222222;
`;
